package lab;

import java.io.IOException;

import javax.servlet.RequestDispatcher;
import javax.servlet.ServletException;
import javax.servlet.annotation.WebServlet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;
@WebServlet("/lotto3")
public class LottoServlet3 extends HttpServlet {
	private static final long serialVersionUID = 1L;
	protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
		String num = request.getParameter("number");
		int num1 = (int)(Math.random()*6+1);
		HttpSession session = request.getSession();
		int[] cunt = (int[])session.getAttribute("cnt");
		if(session.getAttribute("cnt") == null) {
			cunt = new int[1];
			session.setAttribute("cnt", cunt);
		}
		else {
			cunt[0]++;
		}
		if(cunt[0] >=3) {
			 session.setAttribute("msg", "더 이상 응모할 수 없어요… 브라우저를 재가동한 후에는 가능하답니다.");
			 RequestDispatcher rd = request.getRequestDispatcher("/clientexam/lottoResult.jsp");
		     rd.forward(request, response);
	         return;
		}
		
		if (num1 == Integer.parseInt(num)) {
			session.setAttribute("msg", "당첨~~~ 추카포카");
			session.setAttribute("imginfo", 
					"data:image/jpeg;base64,");
		}
		else {
			session.setAttribute("msg", "실패~~~ 아쉬아쉽");
			session.setAttribute("imginfo",
					"https://mblogthumb-phinf.pstatic.net/20150716_115/elqk8802_14370100338676yiqY_PNG/%BF%F4%B1%E4%C2%A9_%BF%F4%B1%E4%B1%D7%B8%B2_%C0%E7%B9%D5%B4%C2%C2%A9_%C0%AF%B8%D3%B8%F0%C0%BD___012.png?type=w420"); 
			System.out.println("전달된 값: " + num);
			System.out.println("추출된 값: " + num1);
		}
		RequestDispatcher rd = request.getRequestDispatcher("/clientexam/lottoResult.jsp");
        rd.forward(request, response);
	}

}
